function setCookie(n,t,i){var r=new Date,u;r.setTime(r.getTime()+i*864e5);u="expires="+r.toUTCString();document.cookie=n+"="+t+";"+u+";path=/"}function getCookie(n){for(var t,r=n+"=",f=decodeURIComponent(document.cookie),u=f.split(";"),i=0;i<u.length;i++){for(t=u[i];t.charAt(0)===" ";)t=t.substring(1);if(t.indexOf(r)===0)return t.substring(r.length,t.length)}return""}function pgsoftwebCookiesOk(){setCookie("cookiesAccepted","true",365);$(".cookies-div").css("display","none")}function pgsoftwebCookiesClose(){setCookie("cookiesAccepted","true",365);$(".cookies-div").css("display","none")}$(document).ready(function(){getCookie("cookiesAccepted")!=="true"&&$(".cookies-div").css("display","block");$(".cookies-ok").click(function(n){n.preventDefault();pgsoftwebCookiesOk()});$(".cookies-cancel, .cookies-close").click(function(n){n.preventDefault();pgsoftwebCookiesClose()})});